import React, { Fragment, useEffect, useState } from "react";
import axios from "axios";
import {
  CircularGauge,
  Scale,
  RangeContainer,
  Range,
  Tooltip,
  Size,
  ValueIndicator,
} from "devextreme-react/circular-gauge";
import {
  SWBodyContainer,
  SWTitleContainer,
  SWTitleDiv1,
  SWTitleDiv2,
  SWContainer,
  SWText,
  SWBodyDiv1,
  SWBodyDiv2,
  SWBodyDiv3,
} from "./styles";
import { TbWorldLatitude, TbWorldLongitude } from "react-icons/tb";
import { BiCurrentLocation } from "react-icons/bi";

const Weather = () => {
  const [weather, setWeather] = useState(false);
  const [location, setLocation] = useState(false);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      getWeather(position.coords.latitude, position.coords.longitude);
      setLocation(true);
    });
  }, []);

  const getWeather = async (lat, long) => {
    let res = await axios.get(
      "http://api.openweathermap.org/data/2.5/weather",
      {
        params: {
          lat: lat,
          lon: long,
          appid: process.env.REACT_APP_OPEN_WHEATHER_KEY,
          lang: "pt",
          units: "metric",
        },
      }
    );
    setWeather(res.data);
  };

  if (location === false) {
    return <Fragment>É necessário ligar a localização no browser o/</Fragment>;
  } else if (weather === false) {
    return <Fragment>Carregando...</Fragment>;
  } else {
    return (
      <SWContainer>
        <SWTitleContainer>
          <SWTitleDiv1>
            <SWText>
              <BiCurrentLocation />
              {weather["name"]}
            </SWText>
            <SWText>({weather["weather"][0]["description"]})</SWText>
          </SWTitleDiv1>
          <SWTitleDiv2>
            <SWText>
              <TbWorldLongitude /> Lon: {weather["coord"]["lon"]}°
            </SWText>
            <SWText>
              <TbWorldLatitude /> Lat: {weather["coord"]["lat"]}°
            </SWText>
          </SWTitleDiv2>
        </SWTitleContainer>
        <SWBodyContainer>
         
            <CircularGauge
            id="gauge"
            value={weather["main"]["temp"]}
            subvalues={[
              weather["main"]["temp_min"],
              weather["main"]["temp_max"],
            ]}
          >
            <Scale startValue={-10} endValue={50} tickInterval={10} />
            <Size height={250} width={250} />
            <RangeContainer palette={"Soft Blue"}>
              <Range startValue={-10} endValue={10} color="#be03fc" />
              <Range startValue={10} endValue={18} color="#0388fc" />
              <Range startValue={18} endValue={28} color="#00bd10" />
              <Range startValue={28} endValue={35} color="#dade04" />
              <Range startValue={35} endValue={50} color="#fc2803" />
            </RangeContainer>
            <Tooltip enabled={true} />
            <ValueIndicator
              type={"rectangleNeedle"}
              width={4}
              color={"#ffffff"}
              spindleSize={10}
            />
          </CircularGauge>
        </SWBodyContainer>
      </SWContainer>
    );
    // return (
    //   <Fragment>
    //     <h3>
    //       Tempo em {weather["name"]}({weather["coord"]["lon"]}°{" "}
    //       {weather["coord"]["lat"]}°) » {weather["weather"][0]["description"]} «
    //     </h3>
    //     <br />
    //     <ul>
    //       <li>Atualizado: {new Date(weather["dt"] * 1000).toUTCString()}</li>
    //       <li>Temperatura atual: {weather["main"]["temp"]}°</li>
    //       <li>Temperatura máxima: {weather["main"]["temp_max"]}°</li>
    //       <li>Temperatura minima: {weather["main"]["temp_min"]}°</li>
    //       <li>Wind speed: {weather["wind"]["speed"]} m/s</li>
    //       <li>Wind direction: {weather["wind"]["deg"]}°</li>
    //       <li>
    //         Sunrise: {new Date(weather["sys"]["sunrise"] * 1000).toUTCString()}
    //       </li>
    //       <li>
    //         Sunset : {new Date(weather["sys"]["sunset"] * 1000).toUTCString()}
    //       </li>
    //     </ul>
    //   </Fragment>
    // );
  }
};

export default Weather;
