import React from "react";
import { SHPContainer1 } from "./pageStyles";
import axios from "axios";

const HomePage = () => {
  const [location, setLocation] = useState(false);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      console.log(position.coords.latitude, position.coords.longitude);
      setLocation(true);
    });
  }, []);
  return (
    <SHPContainer1>
      <p> Calendário </p>
      <br></br>
      <iframe
        title="calendar"
        src="https://bauermedia.pt/cidade/emissaofm"
        height="400px"
        width="400px"
        frameborder="0"
      ></iframe>
      <h3>Clima nas minhas Coordenadas</h3>
      <hr />
      <ul>
        <li>Temperatura atual: x°</li>
        <li>Temperatura máxima: x°</li>
        <li>Temperatura minima: x°</li>
        <li>Pressão: x hpa</li>
        <li>Umidade: x%</li>
      </ul>
    </SHPContainer1>
  );
};

export default HomePage;
