import { useReducer } from "react";
import ReactDOM from "react-dom/client";

const isSideBarOpen = {
  isOpen: true,
};

const reducer = (state, action) => {
  switch (action.type) {
    case "IS_OPEN":
      return (state.isOpen = action.payload);
    default:
      return state;
  }
};
