import { createRef, useEffect } from "react";
import Diagram from "devextreme-react/diagram";
import "whatwg-fetch";

export const DiagramEditor = (props) => {
  const diagramRef = createRef();

  useEffect(() => {
    const diagram = this.diagramRef.current.instance;
    fetch("data/diagram-flow.json")
      .then((response) => response.json())
      .then((json) => {
        diagram.import(JSON.stringify(json));
      })
      .catch(() => {
        throw new Error("Data Loading Error");
      });
  }, []);

  return <Diagram id="diagram" ref={diagramRef} />;
};
