import { createSlice } from "@reduxjs/toolkit";

const sideBarSlice = createSlice({
  name: "sideBar",
  initialState: {
    isOpen: true,
  },
  reducers: {
    stateOfSideBar: {
      reducer: (state, action) => {
        state.isOpen = action.payload.isOpen;
      },
    },
  },
});

const SettingsSelectors = {
  selectAll: (state) => state.isOpen.isOpen,
  selectByKey: (state, stateKey) => state.isOpen.isOpen[stateKey],
};

export const { selectAll, selectByKey } = SettingsSelectors;
export const { stateOfSideBar } = sideBarSlice.actions;
export default sideBarSlice.reducer;
