import React, { Fragment, useEffect, useState } from "react";
import axios from "axios";

const Weather = () => {
  const [weather, setWeather] = useState(false);
  const [location, setLocation] = useState(false);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) => {
      getWeather(position.coords.latitude, position.coords.longitude);
      setLocation(true);
    });
  }, []);

  const getWeather = async (lat, long) => {
    let res = await axios.get(
      "http://api.openweathermap.org/data/2.5/weather",
      {
        params: {
          lat: lat,
          lon: long,
          appid: process.env.REACT_APP_OPEN_WHEATHER_KEY,
          lang: "pt",
          units: "metric",
        },
      }
    );
    setWeather(res.data);
  };

  if (location == false) {
    return (
      <Fragment>Você precisa habilitar a localização no browser o/</Fragment>
    );
  } else if (weather == false) {
    return <Fragment>Carregando o clima...</Fragment>;
  } else {
    return (
      <Fragment>
        <h3>
          Clima nas suas Coordenadas ({weather["weather"][0]["description"]})
        </h3>
        <br />
        <ul>
          <li>Cidade : {weather["name"]}</li>
          <li>
            Coordinates: {weather["coord"]["lon"]}° {weather["coord"]["lat"]}°
          </li>
          <li>
            Atulalizado ás:
            {new Date(weather["dt"] * 1000).toUTCString()}
          </li>
          <li>Temperatura máxima: {weather["main"]["temp_max"]}°</li>
          <li>Temperatura atual: {weather["main"]["temp"]}°</li>
          <li>Temperatura máxima: {weather["main"]["temp_max"]}°</li>
          <li>Temperatura minima: {weather["main"]["temp_min"]}°</li>
          <li>Wind speed: {weather["wind"]["speed"]} m/s</li>
          <li>Wind direction: {weather["wind"]["deg"]}°</li>
          <li>Wind gust: {weather["wind"]["gust"]}m/s</li>

          <li>Sunrise: {weather["sys"]["sunrise"]}</li>
          <li>Sunset : {weather["sys"]["sunset"]}</li>
        </ul>
      </Fragment>
    );
  }
};

export default Weather;
